import React from 'react';
import { GoogleMap, LoadScript, Marker,DirectionsService } from '@react-google-maps/api';


const containerStyle = {
  width: '350px',
  height: '350px'
};

const location = {
    
  lat: 23.833348,
  lng: 90.419049
};  

const onLoad = marker => {
    console.log('marker: ', marker)
  }

const Map = () => {
    return (
        <div className="mapArea">
        <LoadScript
              googleMapsApiKey="AIzaSyAlLvZr-xXGkQuzjj2tSmOzCKQMahFLh4U"
            >
              <GoogleMap
                mapContainerStyle={containerStyle}
                center={location}
                zoom={16}
              >
                { /* Child components, such as markers, info windows, etc. */ }
                {/* <Marker
              onLoad={onLoad}
              position={location}
            /> */}
               <DirectionsService
                  // required
                  options={{ 
                    destination: 'Gulshan-e-Iqbal, Dhaka, Bangladesh',
                    origin: 'Dhanmondi, Dhaka, Bangladesh',
                    travelMode: 'DRIVING'
                  }}
                  // required
                  callback={this.directionsCallback}
                  // optional
                  onLoad={directionsService => {
                    console.log('DirectionsService onLoad directionsService: ', directionsService)
                  }}
                  // optional
                  onUnmount={directionsService => {
                    console.log('DirectionsService onUnmount directionsService: ', directionsService)
                  }}
                />
              </GoogleMap>


           


            </LoadScript>
            </div>
    );
};

export default Map;
